<?php/** * Classe minimaliste pour envoi SMS FREE */class SmsFree{    /**     * URl FREE pour envoi SMS     */    const URL = 'https://smsapi.free-mobile.fr/sendmsg?user={{user}}&pass={{secret}}&msg=';    /**     * Retourne l'url a appeler     * @param string $aUserParams     */    public function getFreeUrl(array $aUserParams)    {        return str_replace(['{{user}}', '{{secret}}'], [$aUserParams['user'], $aUserParams['secret']], static::URL);    }}